def test_follower_parsing():
    follower = Follower(who_to_follow="test_user", html_data=FOLLOWER_DIV)
    assert follower.username == "IWashington1963"
    assert follower.name == "IWashington1963"
    assert follower.who_to_follow == "test_user"


def test_follower_parsing_another():
    follower = Follower(who_to_follow="test_user", html_data=FOLLOWER_DIV_ANOTHER)
    assert follower.username == "BabylonBee"
    assert follower.name == "BabylonBee"
    assert follower.who_to_follow == "test_user"


def test_follower_without_html_data():
    follower = Follower(
        who_to_follow="test_user", username="test_username", name="Test User"
    )
    assert follower.username == "test_username"
    assert follower.name == "Test User"
    assert follower.who_to_follow == "test_user"


def test_follower_missing_username():
    with pytest.raises(ValueError):
        Follower(who_to_follow="test_user")
