    assert p.who_reposted == 'realDonaldTrump'
    assert p.is_repost == True
    assert p.likes == 6220
    assert p.replies == 125
    assert p.reposts == 981

    t_string = "Jan 19, 2025, 2:14 AM"
    assert p.timestamp == datetime.strptime(t_string, "%b %d, %Y, %I:%M %p")

